export default {
  mounted(el) {
    let x = event.x - 50
    let y = event.y - 50
    el.style.transform = "translate("+ event.x / 6 + "px, " + event.y / 6 +"px)"


    document.onmousemove = ((event) => {
      let x = event.x - 50
      let y = event.y - 50

      console.log(x, y)

      el.style.transform = "translate("+ event.x / 6 + "px, " + event.y / 6 +"px)"

    })

  },
  unmounted() {
    console.log('zxc')
  }
};
